package assignment;

public class Nested22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=10;i++) {
			for(j=1;j<=10;j++) {
				System.out.println("the value of i is : "+i+"and the value of j is : "+j);
			}
			System.out.println("*****************");
		}

	}

}
